import React, { useEffect, useState } from 'react';
import axios from '../axios';
import { Container, Typography, Card, CardContent, TextField, Button } from '@mui/material';

const BlogPost = ({ match }) => {
  const [post, setPost] = useState(null);
  const [comments, setComments] = useState([]);
  const [newComment, setNewComment] = useState('');

  useEffect(() => {
    axios.get(`/blog-posts/${match.params.id}`)
      .then(response => setPost(response.data))
      .catch(error => console.error(error));

    axios.get(`/blog-posts/${match.params.id}/comments`)
      .then(response => setComments(response.data))
      .catch(error => console.error(error));
  }, [match.params.id]);

  const handleCommentSubmit = async () => {
    try {
      await axios.post(`/blog-posts/${match.params.id}/comments`, { comment_text: newComment });
      setComments([...comments, { comment_text: newComment }]);
      setNewComment('');
    } catch (error) {
      console.error(error);
    }
  };

  if (!post) return <Typography>Loading...</Typography>;

  return (
    <Container>
      <Typography variant="h4">{post.title}</Typography>
      <Typography variant="body1">{post.content}</Typography>

      <Typography variant="h5" gutterBottom>Comments</Typography>
      {comments.map((comment, index) => (
        <Card key={index} style={{ marginBottom: '16px' }}>
          <CardContent>
            <Typography>{comment.comment_text}</Typography>
          </CardContent>
        </Card>
      ))}

      <TextField
        label="Add a comment"
        value={newComment}
        onChange={(e) => setNewComment(e.target.value)}
        fullWidth
        margin="normal"
      />
      <Button onClick={handleCommentSubmit} variant="contained" color="primary">
        Submit
      </Button>
    </Container>
  );
};

export default BlogPost;
