import React from 'react';
import { Card, CardContent, Typography, Button, CardActions, Box } from '@mui/material';

const BlogPostCard = ({ post, userRole }) => {
  return (
    <Card
      sx={{
        backgroundColor: 'background.paper',
        mb: 2,
        height: '250px',  // Ensure consistent card height
        display: 'flex',
        flexDirection: 'column',
        justifyContent: 'space-between',
      }}
    >
      <CardContent>
        <Typography variant="h5" color="primary" gutterBottom sx={{ whiteSpace: 'nowrap', overflow: 'hidden', textOverflow: 'ellipsis' }}>
          {post.title}
        </Typography>
        <Typography variant="body2" color="text.secondary" sx={{ height: '80px', overflow: 'hidden', textOverflow: 'ellipsis' }}>
          {post.content}
        </Typography>
      </CardContent>

      {(userRole === 'admin' || userRole === 'editor') && (
        <CardActions sx={{ paddingLeft: 2, paddingBottom: 2 }}>
          <Button variant="outlined" color="secondary">
            Edit
          </Button>
          <Button variant="outlined" color="error">
            Delete
          </Button>
        </CardActions>
      )}
    </Card>
  );
};

export default BlogPostCard;
