import React, { useEffect, useState } from 'react';
import axios from '../axios';
import { Container, Typography, Button, CircularProgress, Box } from '@mui/material';
import Grid2 from '@mui/material/Unstable_Grid2';  // Use Grid2 for modern layout
import BlogPostCard from './BlogPostCard';
import { useNavigate } from 'react-router-dom';

const Home = () => {
  const [posts, setPosts] = useState([]);
  const [currentPage, setCurrentPage] = useState(1);
  const [totalPages, setTotalPages] = useState(1);
  const [userRole, setUserRole] = useState('');
  const [loading, setLoading] = useState(true);  // ✅ Add loading state
  const navigate = useNavigate();

  useEffect(() => {
    const token = localStorage.getItem('token');
    const role = localStorage.getItem('role');
    if (!token) {
      window.location.href = '/login';
    } else {
      setUserRole(role);
    }

    fetchPosts(currentPage);
  }, [currentPage]);

  // ✅ Fetch posts and set loading state
  const fetchPosts = async (page) => {
    setLoading(true);  // Start loading
    try {
      const response = await axios.get(`/blog-posts?page=${page}`);
      setPosts(response.data.data);
      setTotalPages(response.data.meta.last_page);
    } catch (error) {
      console.error('Error fetching blog posts:', error);
    } finally {
      setLoading(false);  // Stop loading
    }
  };

  // ✅ Show the spinner while loading
  if (loading) {
    return (
      <Box
        sx={{
          display: 'flex',
          justifyContent: 'center',
          alignItems: 'center',
          height: '100vh',
          backgroundColor: 'background.default',
        }}
      >
        <CircularProgress color="primary" />
      </Box>
    );
  }

  return (
    <Container>
      <Typography variant="h4" gutterBottom>
        Blog Posts
      </Typography>

      {/* Show Create Post button only for Admin and Editor */}
      {(userRole === 'admin' || userRole === 'editor') && (
        <Button
          onClick={() => navigate('/create')}
          variant="contained"
          color="primary"
          style={{ marginBottom: '20px' }}
        >
          Create New Post
        </Button>
      )}

      <Grid2 container spacing={3}>
        {posts.map((post) => (
          <Grid2 item xs={12} md={6} key={post.id}>
            <BlogPostCard post={post} userRole={userRole} />
          </Grid2>
        ))}
      </Grid2>

      {/* Pagination */}
      <div style={{ textAlign: 'center', marginTop: '20px' }}>
        {Array.from({ length: totalPages }).map((_, index) => (
          <Button
            key={index}
            variant={index + 1 === currentPage ? 'contained' : 'outlined'}
            onClick={() => setCurrentPage(index + 1)}
          >
            {index + 1}
          </Button>
        ))}
      </div>
    </Container>
  );
};

export default Home;
