import React, { useEffect, useState } from "react";
import axios from "../axios";
import {
  Container,
  Typography,
  Button,
  CircularProgress,
  Box,
  Grid,
  Alert,
  Stack,
  IconButton,
  Menu,
  MenuItem,
  Card,
  CardContent,
  CardActions,
  CardMedia,
} from "@mui/material";
import MoreVertIcon from "@mui/icons-material/MoreVert";
import ChatBubbleOutlineIcon from "@mui/icons-material/ChatBubbleOutline";
import { useNavigate } from "react-router-dom";

const Home = () => {
  const [posts, setPosts] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");
  const [anchorEl, setAnchorEl] = useState(null);
  const [selectedPost, setSelectedPost] = useState(null);

  const navigate = useNavigate();

  useEffect(() => {
    const fetchPosts = async () => {
      setLoading(true);
      try {
        const response = await axios.get("/blog-posts");
        
        // Access the correct structure of response data
        if (response.data && response.data.data) {
          setPosts(response.data.data);
        } else {
          setPosts([]);
        }
      } catch (error) {
        setError("Failed to load blog posts.");
      } finally {
        setLoading(false);
      }
    };
  
    fetchPosts();
  }, []);
  

  const fetchPosts = async () => {
    setLoading(true);
    try {
      const response = await axios.get("/blog-posts");
      setPosts(response.data);
    } catch (error) {
      setError("Failed to load blog posts.");
    } finally {
      setLoading(false);
    }
  };

  const handleMenuClick = (event, post) => {
    setAnchorEl(event.currentTarget);
    setSelectedPost(post);
  };

  const handleMenuClose = () => {
    setAnchorEl(null);
  };

  const handleEdit = () => {
    navigate(`/edit/${selectedPost.id}`);
    handleMenuClose();
  };

  const handleDelete = async () => {
    try {
      await axios.delete(`/blog-posts/${selectedPost.id}`);
      setPosts(posts.filter((p) => p.id !== selectedPost.id));
      handleMenuClose();
    } catch (error) {
      console.error("Error deleting post:", error);
    }
  };

  if (loading) {
    return (
      <Box sx={{ display: "flex", justifyContent: "center", height: "60vh" }}>
        <CircularProgress />
      </Box>
    );
  }

  if (error) {
    return <Alert severity="error">{error}</Alert>;
  }

  return (
    <Container>
      <Typography variant="h2" gutterBottom align="center">
        THE BLOG
      </Typography>
      <Grid container spacing={3}>
        {posts.map((post) => (
          <Grid item xs={12} md={6} lg={4} key={post.id}>
            <Card>
              <CardMedia
                component="img"
                height="200"
                image={post.image || "https://via.placeholder.com/200"}
                alt={post.title}
              />
              <CardContent>
                <Typography variant="h5" gutterBottom>
                  {post.title}
                </Typography>
                <Typography variant="body2" color="text.secondary" noWrap>
                  {post.content.substring(0, 100)}...
                </Typography>
              </CardContent>
              <CardActions>
                <Button
                  onClick={() => navigate(`/posts/${post.id}`)}
                  variant="contained"
                >
                  Read More
                </Button>
                <IconButton onClick={() => navigate(`/posts/${post.id}`)} color="primary">
                  <ChatBubbleOutlineIcon />
                  <Typography variant="body2" sx={{ ml: 1 }}>
                    {post.comments?.length || 0}
                  </Typography>
                </IconButton>
                <IconButton onClick={(e) => handleMenuClick(e, post)}>
                  <MoreVertIcon />
                </IconButton>
              </CardActions>
            </Card>
          </Grid>
        ))}
      </Grid>
      <Menu anchorEl={anchorEl} open={Boolean(anchorEl)} onClose={handleMenuClose}>
        <MenuItem onClick={handleEdit}>Edit</MenuItem>
        <MenuItem onClick={handleDelete}>Delete</MenuItem>
      </Menu>
    </Container>
  );
};

export default Home;
