import React, { useState } from 'react';
import {
  Card,
  CardContent,
  Typography,
  Button,
  CardActions,
  Dialog,
  DialogActions,
  DialogContent,
  DialogTitle,
  TextField,
} from '@mui/material';
import axios from '../axios';

const BlogPostCard = ({ post, userRole, onPostDeleted, onPostUpdated }) => {
  const [editMode, setEditMode] = useState(false);
  const [title, setTitle] = useState(post.title);
  const [content, setContent] = useState(post.content);

  const handleDelete = async () => {
    try {
      const token = localStorage.getItem('token');
      await axios.delete(`/blog-posts/${post.id}`, {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      });
      onPostDeleted(post.id);
    } catch (error) {
      console.error('Failed to delete the post:', error);
    }
  };

  const handleEdit = async () => {
    try {
      const token = localStorage.getItem('token');
      await axios.put(
        `/blog-posts/${post.id}`,
        { title, content },
        {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        }
      );
      setEditMode(false);
      onPostUpdated(post.id, { title, content });
    } catch (error) {
      console.error('Failed to update the post:', error);
    }
  };

  return (
    <Card
      sx={{
        backgroundColor: 'background.paper',
        mb: 2,
        height: '250px',
        display: 'flex',
        flexDirection: 'column',
        justifyContent: 'space-between',
      }}
    >
      <CardContent>
        <Typography
          variant="h5"
          color="primary"
          gutterBottom
          sx={{ whiteSpace: 'nowrap', overflow: 'hidden', textOverflow: 'ellipsis' }}
        >
          {title}
        </Typography>
        <Typography
          variant="body2"
          color="text.secondary"
          sx={{ height: '80px', overflow: 'hidden', textOverflow: 'ellipsis' }}
        >
          {content}
        </Typography>

        {/* âœ… Show Author's Name */}
        <Typography variant="body2" color="text.secondary">
          Posted by: <strong>{post.user?.name || 'Unknown'}</strong>
        </Typography>
      </CardContent>

      {(userRole === 'admin' || userRole === 'editor') && (
        <CardActions sx={{ paddingLeft: 2, paddingBottom: 2 }}>
          <Button variant="outlined" color="secondary" onClick={() => setEditMode(true)}>
            Edit
          </Button>
          <Button variant="outlined" color="error" onClick={handleDelete}>
            Delete
          </Button>
        </CardActions>
      )}

      {/* Edit Post Dialog */}
      <Dialog open={editMode} onClose={() => setEditMode(false)}>
        <DialogTitle>Edit Post</DialogTitle>
        <DialogContent>
          <TextField
            label="Title"
            fullWidth
            margin="normal"
            value={title}
            onChange={(e) => setTitle(e.target.value)}
          />
          <TextField
            label="Content"
            fullWidth
            margin="normal"
            multiline
            rows={4}
            value={content}
            onChange={(e) => setContent(e.target.value)}
          />
        </DialogContent>
        <DialogActions>
          <Button onClick={() => setEditMode(false)} color="secondary">
            Cancel
          </Button>
          <Button onClick={handleEdit} variant="contained" color="primary">
            Save
          </Button>
        </DialogActions>
      </Dialog>
    </Card>
  );
};

export default BlogPostCard;
