import React, { useState, useEffect } from 'react';
import {
  Dialog,
  DialogTitle,
  DialogContent,
  TextField,
  Button,
  List,
  ListItem,
  ListItemText,
  DialogActions,
  Box,
  Typography,
} from '@mui/material';
import axios from '../axios';

const CommentDialog = ({ postId, open, onClose }) => {
  const [comments, setComments] = useState([]);
  const [newComment, setNewComment] = useState('');

  // ✅ Fetch comments when the dialog opens
  useEffect(() => {
    if (open) {
      fetchComments();
    }
  }, [open]);

  const fetchComments = async () => {
    try {
      const response = await axios.get(`/blog-posts/${postId}/comments`);
      setComments(response.data.data);
    } catch (error) {
      console.error('Error fetching comments:', error);
    }
  };

  const handleAddComment = async () => {
    if (!newComment.trim()) return;
  
    try {
      const token = localStorage.getItem('token');
      await axios.post(
        `/blog-posts/${postId}/comments`,
        { comment_text: newComment },
        { headers: { Authorization: `Bearer ${token}` } }
      );
  
      setNewComment('');
      // ✅ Fetch comments again to update the state with the full user data
      fetchComments();
    } catch (error) {
      console.error('Error adding comment:', error);
    }
  };
  

  return (
    <Dialog open={open} onClose={onClose} fullWidth maxWidth="sm">
      <DialogTitle>Comments</DialogTitle>
      <DialogContent>
        {comments.length === 0 ? (
          // ✅ No comments message
          <Typography variant="body1" color="text.secondary">
            No comments yet. Be the first to comment!
          </Typography>
        ) : (
          <List>
            {comments.map((comment) => (
              <ListItem key={comment.id}>
                <ListItemText
                  primary={comment.user ? comment.user.name : 'Unknown User'}
                  secondary={comment.comment_text}
                />
              </ListItem>
            ))}
          </List>
        )}
        <Box mt={2}>
          <TextField
            label="Add a comment"
            fullWidth
            multiline
            rows={2}
            value={newComment}
            onChange={(e) => setNewComment(e.target.value)}
            sx={{
              input: { color: 'white' },
              '& .MuiOutlinedInput-root': {
                '& fieldset': {
                  borderColor: '#90caf9',
                },
                '&:hover fieldset': {
                  borderColor: '#64b5f6',
                },
                '&.Mui-focused fieldset': {
                  borderColor: '#42a5f5',
                },
              },
            }}
          />
        </Box>
      </DialogContent>
      <DialogActions>
        <Button onClick={onClose} color="secondary">
          Close
        </Button>
        <Button onClick={handleAddComment} variant="contained" color="primary">
          Post Comment
        </Button>
      </DialogActions>
    </Dialog>
  );
};

export default CommentDialog;
