import React, { useEffect, useState } from 'react';
import axios from '../axios';
import { Container, Typography, Card, CardContent, Button } from '@mui/material';

const Home = () => {
  const [posts, setPosts] = useState([]);

  useEffect(() => {
    axios.get('/blog-posts')
      .then(response => {
        console.log(response.data);  // ✅ Check the response
        setPosts(response.data.data);
      })
      .catch(error => {
        console.error('Error fetching blog posts:', error);
      });
  }, []);

  return (
    <Container>
      <Typography variant="h4" gutterBottom>
        Blog Posts
      </Typography>
      {posts.length > 0 ? (
        posts.map(post => (
          <Card key={post.id} style={{ marginBottom: '16px' }}>
            <CardContent>
              <Typography variant="h5">{post.title}</Typography>
              <Typography variant="body2">{post.content.substring(0, 100)}...</Typography>
              <Button href={`/posts/${post.id}`} variant="contained" color="primary">
                Read More
              </Button>
            </CardContent>
          </Card>
        ))
      ) : (
        <Typography>No blog posts available.</Typography>
      )}
    </Container>
  );
};

export default Home;
