import React from 'react';
import { Card, CardContent, Typography, Button, CardActions } from '@mui/material';
import { useNavigate } from 'react-router-dom';
import axios from '../axios';

const BlogPostCard = ({ post, userRole }) => {
  const navigate = useNavigate();

  const handleDelete = async () => {
    try {
      await axios.delete(`/blog-posts/${post.id}`);
      alert('Post deleted successfully');
      window.location.reload();
    } catch (error) {
      alert('Error deleting post: ' + error.message);
    }
  };

  return (
    <Card>
      <CardContent>
        <Typography variant="h5">{post.title}</Typography>
        <Typography variant="body2">{post.content.substring(0, 100)}...</Typography>
      </CardContent>
      <CardActions>
        {/* Show Edit/Delete buttons for Admin and Editor */}
        {(userRole === 'admin' || userRole === 'editor') && (
          <>
            <Button onClick={() => navigate(`/edit/${post.id}`)} variant="outlined" color="secondary">
              Edit
            </Button>
            <Button onClick={handleDelete} variant="outlined" color="error">
              Delete
            </Button>
          </>
        )}
      </CardActions>
    </Card>
  );
};

export default BlogPostCard;
