import React, { useState, useEffect } from "react";
import {
  Card,
  CardContent,
  Typography,
  Button,
  CardActions,
  IconButton,
  Snackbar,
  Alert,
  Dialog,
  DialogTitle,
  DialogContent,
  TextField,
  DialogActions,
  CardMedia,
  Box,
  Badge,
} from "@mui/material";
import axios from "../axios";
import CommentDialog from "./CommentDialog";
import ChatBubbleOutlineIcon from "@mui/icons-material/ChatBubbleOutline";

const BlogPostCard = ({ post, userRole, onPostDeleted, onPostUpdated }) => {
  const [editMode, setEditMode] = useState(false);
  const [commentsOpen, setCommentsOpen] = useState(false);
  const [authorName, setAuthorName] = useState("Unknown");
  const [commentCount, setCommentCount] = useState(0);

  // Form state for editing
  const [title, setTitle] = useState(post.title);
  const [content, setContent] = useState(post.content);
  const [bigContent, setBigContent] = useState(post.big_content);

  // Snackbar state
  const [snackbar, setSnackbar] = useState({
    open: false,
    message: "",
    severity: "success",
  });

  useEffect(() => {
    const fetchAuthor = async () => {
      try {
        const response = await axios.get(`/users/${post.user_id}`);
        setAuthorName(response.data.name);
      } catch (error) {
        console.error("Failed to fetch the author's name:", error);
      }
    };

    const fetchCommentCount = async () => {
      try {
        const response = await axios.get(`/blog-posts/${post.id}/comments`);
        setCommentCount(response.data.length);
      } catch (error) {
        console.error("Failed to fetch comment count:", error);
      }
    };

    if (post.user_id) {
      fetchAuthor();
    }
    fetchCommentCount();
  }, [post.user_id, post.id]);

  return (
    <Card
      sx={{
        backgroundColor: "background.paper",
        borderRadius: 2,
        boxShadow: 3,
        mb: 3,
        display: "flex",
        flexDirection: "column",
        height: "100%", // Ensure cards take up equal height
      }}
    >
      <CardMedia
        component="div"
        sx={{
          height: 200,
          backgroundSize: "cover",
          backgroundPosition: "center",
          backgroundImage: `url("https://uscredentialing.com/wp-content/uploads/2014/06/placeholder.png")`,
        }}
        alt={post.title}
      />
      <CardContent>
        <Typography variant="h6" sx={{ fontWeight: "bold" }}>
          {post.title}
        </Typography>
        <Typography variant="body2" color="text.secondary" sx={{ mb: 1 }}>
          {post.content.substring(0, 100)}...
        </Typography>
        <Typography variant="caption" color="text.secondary">
          Posted by: {authorName}
        </Typography>
      </CardContent>

      <CardActions
        sx={{
          justifyContent: "space-between",
          alignItems: "center",
          px: 2,
          mt: "auto",
          mb: 2,
        }}
      >
        <Button
          size="small"
          variant="outlined"
          color="primary"
          onClick={() => (window.location.href = `/posts/${post.id}`)}
        >
          Read More
        </Button>
        <Box>
          {userRole === "admin" && (
            <>
              <Button
                onClick={() => setEditMode(true)}
                color="secondary"
                size="small"
              >
                Edit
              </Button>
              <Button onClick={() => handleDelete()} color="error" size="small">
                Delete
              </Button>
            </>
          )}
          {userRole === "editor" && (
            <Button
              onClick={() => setEditMode(true)}
              color="secondary"
              size="small"
            >
              Edit
            </Button>
          )}
          <IconButton onClick={() => setCommentsOpen(true)} color="primary">
            <Badge badgeContent={commentCount} color="secondary">
              <ChatBubbleOutlineIcon />
            </Badge>
          </IconButton>
        </Box>
      </CardActions>

      <CommentDialog
        postId={post.id}
        open={commentsOpen}
        onClose={() => setCommentsOpen(false)}
      />

      <Snackbar
        open={snackbar.open}
        autoHideDuration={3000}
        onClose={() => setSnackbar({ ...snackbar, open: false })}
      >
        <Alert
          onClose={() => setSnackbar({ ...snackbar, open: false })}
          severity={snackbar.severity}
          sx={{ width: "100%" }}
        >
          {snackbar.message}
        </Alert>
      </Snackbar>
    </Card>
  );
};

export default BlogPostCard;
