import React, { useState } from 'react';
import { Card, CardContent, Typography, Button, CardActions, Dialog, DialogActions, DialogContent, DialogTitle, TextField, Snackbar, Alert } from '@mui/material';
import axios from '../axios';

const BlogPostCard = ({ post, userRole, onPostDeleted, onPostUpdated }) => {
  const [editMode, setEditMode] = useState(false);
  const [title, setTitle] = useState(post.title);
  const [content, setContent] = useState(post.content);
  const [snackbar, setSnackbar] = useState({ open: false, message: '', severity: 'success' });

  // ✅ Handle Post Deletion
  const handleDelete = async () => {
    try {
      const token = localStorage.getItem('token');
      await axios.delete(`/blog-posts/${post.id}`, {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      });

      setSnackbar({ open: true, message: 'Post deleted successfully!', severity: 'success' });
      onPostDeleted(post.id);
    } catch (error) {
      setSnackbar({ open: true, message: `Failed to delete the post. Error: ${error.response.status}`, severity: 'error' });
    }
  };

  // ✅ Handle Post Edit
  const handleEdit = async () => {
    try {
      const token = localStorage.getItem('token');
      await axios.put(
        `/blog-posts/${post.id}`,
        { title, content },
        {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        }
      );

      setSnackbar({ open: true, message: 'Post updated successfully!', severity: 'success' });
      setEditMode(false);
      onPostUpdated(post.id, { title, content });
    } catch (error) {
      setSnackbar({ open: true, message: `Failed to update the post. Error: ${error.response.status}`, severity: 'error' });
    }
  };

  // ✅ Handle Snackbar Close
  const handleCloseSnackbar = () => {
    setSnackbar({ ...snackbar, open: false });
  };

  return (
    <Card
      sx={{
        backgroundColor: 'background.paper',
        mb: 2,
        height: '250px',
        display: 'flex',
        flexDirection: 'column',
        justifyContent: 'space-between',
      }}
    >
      <CardContent>
        <Typography variant="h5" color="primary" gutterBottom sx={{ whiteSpace: 'nowrap', overflow: 'hidden', textOverflow: 'ellipsis' }}>
          {title}
        </Typography>
        <Typography variant="body2" color="text.secondary" sx={{ height: '80px', overflow: 'hidden', textOverflow: 'ellipsis' }}>
          {content}
        </Typography>
      </CardContent>

      {(userRole === 'admin' || userRole === 'editor') && (
        <CardActions sx={{ paddingLeft: 2, paddingBottom: 2 }}>
          <Button variant="outlined" color="secondary" onClick={() => setEditMode(true)}>
            Edit
          </Button>
          <Button variant="outlined" color="error" onClick={handleDelete}>
            Delete
          </Button>
        </CardActions>
      )}

      {/* Edit Post Dialog */}
      <Dialog open={editMode} onClose={() => setEditMode(false)}>
        <DialogTitle>Edit Post</DialogTitle>
        <DialogContent>
          <TextField
            label="Title"
            fullWidth
            margin="normal"
            value={title}
            onChange={(e) => setTitle(e.target.value)}
            InputLabelProps={{ style: { color: '#b0bec5' } }}
            sx={{
              input: { color: 'white' },
              '& .MuiOutlinedInput-root': {
                '& fieldset': {
                  borderColor: '#90caf9',
                },
                '&:hover fieldset': {
                  borderColor: '#64b5f6',
                },
                '&.Mui-focused fieldset': {
                  borderColor: '#42a5f5',
                },
              },
            }}
          />
          <TextField
            label="Content"
            fullWidth
            margin="normal"
            multiline
            rows={4}
            value={content}
            onChange={(e) => setContent(e.target.value)}
            InputLabelProps={{ style: { color: '#b0bec5' } }}
            sx={{
              textarea: { color: 'white' },
              '& .MuiOutlinedInput-root': {
                '& fieldset': {
                  borderColor: '#90caf9',
                },
                '&:hover fieldset': {
                  borderColor: '#64b5f6',
                },
                '&.Mui-focused fieldset': {
                  borderColor: '#42a5f5',
                },
              },
            }}
          />
        </DialogContent>
        <DialogActions>
          <Button onClick={() => setEditMode(false)} color="secondary">
            Cancel
          </Button>
          <Button onClick={handleEdit} variant="contained" color="primary">
            Save
          </Button>
        </DialogActions>
      </Dialog>

      {/* Snackbar for feedback messages */}
      <Snackbar open={snackbar.open} autoHideDuration={3000} onClose={handleCloseSnackbar}>
        <Alert onClose={handleCloseSnackbar} severity={snackbar.severity} sx={{ width: '100%' }}>
          {snackbar.message}
        </Alert>
      </Snackbar>
    </Card>
  );
};

export default BlogPostCard;
