import React, { useEffect, useState } from 'react';
import axios from '../axios';
import { Container, Typography, Button, CircularProgress, Box, Grid } from '@mui/material';
import BlogPostCard from './BlogPostCard';
import { useNavigate } from 'react-router-dom';

const Home = () => {
  const [posts, setPosts] = useState([]);
  const [currentPage, setCurrentPage] = useState(1);
  const [totalPages, setTotalPages] = useState(1);
  const [userRole, setUserRole] = useState('');
  const [loading, setLoading] = useState(true);
  const navigate = useNavigate();

  useEffect(() => {
    const token = localStorage.getItem('token');
    const role = localStorage.getItem('role');
    if (!token) {
      window.location.href = '/login';
    } else {
      setUserRole(role);
    }

    fetchPosts(currentPage);
  }, [currentPage]);

  const fetchPosts = async (page) => {
    setLoading(true);
    try {
      const response = await axios.get(`/blog-posts?page=${page}`);
      setPosts(response.data.data);
      setTotalPages(response.data.meta.last_page);
    } catch (error) {
      console.error('Error fetching blog posts:', error);
    } finally {
      setLoading(false);
    }
  };

  // ✅ Handle Post Deletion and Pagination Update
  const handlePostDeleted = async (postId) => {
    setPosts((prevPosts) => prevPosts.filter((post) => post.id !== postId));

    // Check if we need to fetch more posts to fill the empty spot
    if (posts.length <= 5 && currentPage < totalPages) {
      const nextPage = currentPage + 1;
      try {
        const response = await axios.get(`/blog-posts?page=${nextPage}`);
        setPosts((prevPosts) => [...prevPosts, ...response.data.data]);
        if (response.data.data.length === 0) {
          setTotalPages((prev) => prev - 1); // Adjust total pages
        }
      } catch (error) {
        console.error('Error fetching additional posts:', error);
      }
    }

    // Remove the page button if no more posts exist on that page
    if (posts.length === 1 && currentPage === totalPages) {
      setTotalPages((prev) => prev - 1);
      if (currentPage > 1) setCurrentPage((prev) => prev - 1);
    }
  };

  // ✅ Handle Post Update
  const handlePostUpdated = (postId, updatedPost) => {
    setPosts((prevPosts) =>
      prevPosts.map((post) => (post.id === postId ? { ...post, ...updatedPost } : post))
    );
  };

  if (loading) {
    return (
      <Box
        sx={{
          display: 'flex',
          justifyContent: 'center',
          alignItems: 'center',
          height: '100vh',
          backgroundColor: 'background.default',
        }}
      >
        <CircularProgress color="primary" />
      </Box>
    );
  }

  return (
    <Container>
      <Typography variant="h4" gutterBottom>
        Blog Posts
      </Typography>

      {(userRole === 'admin' || userRole === 'editor') && (
        <Button
          onClick={() => navigate('/create')}
          variant="contained"
          color="primary"
          style={{ marginBottom: '20px' }}
        >
          Create New Post
        </Button>
      )}

      <Grid container spacing={3}>
        {posts.map((post) => (
          <Grid item xs={12} md={6} key={post.id}>
            <BlogPostCard
              post={post}
              userRole={userRole}
              onPostDeleted={handlePostDeleted}
              onPostUpdated={handlePostUpdated}
            />
          </Grid>
        ))}
      </Grid>

      {/* Pagination Buttons */}
      {totalPages > 1 && (
        <Box textAlign="center" mt={3}>
          {Array.from({ length: totalPages }).map((_, index) => (
            <Button
              key={index}
              variant={index + 1 === currentPage ? 'contained' : 'outlined'}
              onClick={() => setCurrentPage(index + 1)}
            >
              {index + 1}
            </Button>
          ))}
        </Box>
      )}
    </Container>
  );
};

export default Home;
