import React, { useState } from "react";
import { Card, CardContent, Typography, Button, IconButton, Menu, MenuItem } from "@mui/material";
import MoreVertIcon from "@mui/icons-material/MoreVert";
import ChatBubbleOutlineIcon from "@mui/icons-material/ChatBubbleOutline";
import { Link } from "react-router-dom";

const BlogPostCard = ({ post, userRole, onPostDeleted, onPostUpdated }) => {
  const [anchorEl, setAnchorEl] = useState(null);

  const handleMenuClick = (event) => {
    setAnchorEl(event.currentTarget);
  };

  const handleMenuClose = () => {
    setAnchorEl(null);
  };

  return (
    <Card sx={{ backgroundColor: "background.paper", mb: 2 }}>
      <CardContent>
        <Typography variant="h5" gutterBottom>
          {post.title}
        </Typography>
        <Typography variant="body2" color="text.secondary" noWrap>
          {post.content.substring(0, 100)}...
        </Typography>
      </CardContent>

      <CardContent>
        <Button component={Link} to={`/posts/${post.id}`} variant="contained">
          Read More
        </Button>
        <IconButton component={Link} to={`/posts/${post.id}`} color="primary">
          <ChatBubbleOutlineIcon />
          <Typography variant="body2" sx={{ ml: 1 }}>
            {post.comments.length}
          </Typography>
        </IconButton>

        {userRole === "admin" || userRole === "editor" ? (
          <>
            <IconButton onClick={handleMenuClick}>
              <MoreVertIcon />
            </IconButton>
            <Menu anchorEl={anchorEl} open={Boolean(anchorEl)} onClose={handleMenuClose}>
              <MenuItem onClick={() => onPostUpdated(post.id)}>Edit</MenuItem>
              <MenuItem onClick={() => onPostDeleted(post.id)}>Delete</MenuItem>
            </Menu>
          </>
        ) : null}
      </CardContent>
    </Card>
  );
};

export default BlogPostCard;
