import React, { useState } from 'react';
import axios from '../axios';
import { Container, TextField, Typography, Box, Paper, Snackbar, Alert,Button } from '@mui/material';
import { LoadingButton } from '@mui/lab'; // ✅ Import LoadingButton
import { useNavigate } from 'react-router-dom';

const AuthForm = () => {
  const [isLogin, setIsLogin] = useState(true);
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [name, setName] = useState('');
  const [loading, setLoading] = useState(false); // ✅ Add loading state
  const [snackbar, setSnackbar] = useState({ open: false, message: '', severity: 'success' });
  const navigate = useNavigate();

  const handleCloseSnackbar = () => {
    setSnackbar({ ...snackbar, open: false });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);

    try {
      const endpoint = isLogin ? '/login' : '/register';
      const payload = isLogin ? { email, password } : { name, email, password };
      const response = await axios.post(endpoint, payload);

      localStorage.setItem('token', response.data.token);
      setSnackbar({ open: true, message: isLogin ? 'Login successful!' : 'Registration successful!', severity: 'success' });
      navigate('/home');
    } catch (error) {
      setSnackbar({ open: true, message: 'Login/Register failed: ' + error.message, severity: 'error' });
    } finally {
      setLoading(false);
    }
  };

  return (
    <Container maxWidth="xs" sx={{ height: '100vh', display: 'flex', alignItems: 'center', justifyContent: 'center' }}>
      <Paper elevation={4} sx={{ padding: 4, backgroundColor: 'background.paper' }}>
        <Box textAlign="center">
          <Typography variant="h4" gutterBottom color="primary">
            {isLogin ? 'Login' : 'Register'}
          </Typography>
        </Box>

        <form onSubmit={handleSubmit}>
          {!isLogin && (
            <TextField
              label="Name"
              fullWidth
              margin="normal"
              value={name}
              onChange={(e) => setName(e.target.value)}
              InputLabelProps={{ style: { color: '#b0bec5' } }}
            />
          )}
          <TextField
            label="Email"
            type="email"
            fullWidth
            margin="normal"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            InputLabelProps={{ style: { color: '#b0bec5' } }}
          />
          <TextField
            label="Password"
            type="password"
            fullWidth
            margin="normal"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            InputLabelProps={{ style: { color: '#b0bec5' } }}
          />

          {/* ✅ Replace Button with LoadingButton */}
          <LoadingButton
            type="submit"
            variant="contained"
            color="primary"
            fullWidth
            loading={loading}
            sx={{ mt: 2 }}
          >
            {isLogin ? 'Login' : 'Register'}
          </LoadingButton>
        </form>

        <Button onClick={() => setIsLogin(!isLogin)} sx={{ mt: 2, color: 'secondary.main' }}>
          {isLogin ? "Don't have an account? Register" : 'Already have an account? Login'}
        </Button>
      </Paper>

      <Snackbar open={snackbar.open} autoHideDuration={3000} onClose={handleCloseSnackbar}>
        <Alert onClose={handleCloseSnackbar} severity={snackbar.severity} sx={{ width: '100%' }}>
          {snackbar.message}
        </Alert>
      </Snackbar>
    </Container>
  );
};

export default AuthForm;
