import React, { useEffect, useState } from 'react';
import axios from '../axios';
import {
  Container,
  Typography,
  Button,
  CircularProgress,
  Box,
  Grid,
  Snackbar,
  Alert,
} from '@mui/material';
import BlogPostCard from './BlogPostCard';
import { useNavigate } from 'react-router-dom';

const Home = () => {
  const [posts, setPosts] = useState([]);
  const [currentPage, setCurrentPage] = useState(1);
  const [totalPages, setTotalPages] = useState(1);
  const [userRole, setUserRole] = useState('');
  const [loading, setLoading] = useState(true);
  const [snackbar, setSnackbar] = useState({ open: false, message: '', severity: 'success' });
  const navigate = useNavigate();

  useEffect(() => {
    const token = localStorage.getItem('token');
    const role = localStorage.getItem('role');
    if (!token) {
      window.location.href = '/login';
    } else {
      setUserRole(role);
    }

    fetchPosts(currentPage);
  }, [currentPage]);

  const fetchPosts = async (page) => {
    setLoading(true);
    try {
      const response = await axios.get(`/blog-posts?page=${page}`);
      setPosts(response.data.data);
      setTotalPages(response.data.meta.last_page);
    } catch (error) {
      setSnackbar({ open: true, message: 'Error fetching blog posts!', severity: 'error' });
    } finally {
      setLoading(false);
    }
  };

  // ✅ Handle Post Deletion
  const handlePostDeleted = (postId) => {
    setPosts((prevPosts) => prevPosts.filter((post) => post.id !== postId));
    setSnackbar({ open: true, message: 'Post deleted successfully!', severity: 'success' });
  };

  // ✅ Handle Post Update
  const handlePostUpdated = (postId, updatedPost) => {
    setPosts((prevPosts) =>
      prevPosts.map((post) => (post.id === postId ? { ...post, ...updatedPost } : post))
    );
    setSnackbar({ open: true, message: 'Post updated successfully!', severity: 'success' });
  };

  // ✅ Close Snackbar
  const handleCloseSnackbar = () => {
    setSnackbar({ ...snackbar, open: false });
  };

  if (loading) {
    return (
      <Box
        sx={{
          display: 'flex',
          justifyContent: 'center',
          alignItems: 'center',
          height: '100vh',
          backgroundColor: 'background.default',
        }}
      >
        <CircularProgress color="primary" />
      </Box>
    );
  }

  return (
    <Container>
      <Typography variant="h4" gutterBottom>
        Blog Posts
      </Typography>

      {(userRole === 'admin' || userRole === 'editor') && (
        <Button
          onClick={() => navigate('/create')}
          variant="contained"
          color="primary"
          style={{ marginBottom: '20px' }}
        >
          Create New Post
        </Button>
      )}

      <Grid container spacing={3}>
        {posts.map((post) => (
          <Grid item xs={12} md={6} key={post.id}>
            <BlogPostCard
              post={post}
              userRole={userRole}
              onPostDeleted={handlePostDeleted}
              onPostUpdated={handlePostUpdated}
            />
          </Grid>
        ))}
      </Grid>

      <div style={{ textAlign: 'center', marginTop: '20px' }}>
        {Array.from({ length: totalPages }).map((_, index) => (
          <Button
            key={index}
            variant={index + 1 === currentPage ? 'contained' : 'outlined'}
            onClick={() => setCurrentPage(index + 1)}
          >
            {index + 1}
          </Button>
        ))}
      </div>

      {/* Snackbar for feedback messages */}
      <Snackbar open={snackbar.open} autoHideDuration={3000} onClose={handleCloseSnackbar}>
        <Alert onClose={handleCloseSnackbar} severity={snackbar.severity} sx={{ width: '100%' }}>
          {snackbar.message}
        </Alert>
      </Snackbar>
    </Container>
  );
};

export default Home;
