import React, { useState } from 'react';
import axios from '../axios';
import { 
  Container, 
  TextField, 
  Button, 
  Typography, 
  Box, 
  Snackbar, 
  Alert, 
  Paper, 
  CircularProgress, 
  Divider
} from '@mui/material';
import GitHubIcon from '@mui/icons-material/GitHub';
import { useNavigate, useParams } from 'react-router-dom';

const AuthForm = () => {
  const { action } = useParams();
  const isLogin = action === 'login'; 

  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [name, setName] = useState('');
  const [loading, setLoading] = useState(false);
  const [snackbar, setSnackbar] = useState({ open: false, message: '', severity: 'success' });
  const navigate = useNavigate();

  const handleCloseSnackbar = () => {
    setSnackbar({ ...snackbar, open: false });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);

    // Email validation
    const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
    if (!emailRegex.test(email)) {
      setSnackbar({ open: true, message: 'Invalid email format. Please enter a valid email address.', severity: 'error' });
      setLoading(false);
      return;
    }

    try {
      const endpoint = isLogin ? '/login' : '/register';
      const payload = isLogin ? { email, password } : { name, email, password };
      const response = await axios.post(endpoint, payload);

      if (isLogin) {
        localStorage.setItem('token', response.data.token);
        setSnackbar({ open: true, message: 'Login successful!', severity: 'success' });
        navigate('/home');
      } else {
        setSnackbar({ open: true, message: 'Registration successful! Please log in.', severity: 'success' });
        navigate('/auth/login');
      }
    } catch (error) {
      setSnackbar({ open: true, message: 'Authentication failed. Please check your details.', severity: 'error' });
    } finally {
      setLoading(false);
    }
  };

  return (
    <Container 
      maxWidth="xs" 
      sx={{ 
        height: '100vh', 
        display: 'flex', 
        alignItems: 'center', 
        justifyContent: 'center', 
        backgroundColor: 'background.default' 
      }}
    >
      <Paper 
        elevation={4} 
        sx={{ 
          padding: 4, 
          backgroundColor: 'background.paper', 
          textAlign: 'center' 
        }}
      >
  
        <Typography variant="h5" color="primary" gutterBottom>
          {isLogin ? 'Sign in to Your Blog' : 'Create a New Account'}
        </Typography>

        <Box component="form" onSubmit={handleSubmit} sx={{ mt: 2 }}>
          {!isLogin && (
            <TextField
              label="Name"
              fullWidth
              margin="normal"
              value={name}
              onChange={(e) => setName(e.target.value)}
              InputLabelProps={{ style: { color: '#b0bec5' } }}
            />
          )}
          <TextField
            label="Email"
            type="email"
            fullWidth
            margin="normal"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            InputLabelProps={{ style: { color: '#b0bec5' } }}
          />
          <TextField
            label="Password"
            type="password"
            fullWidth
            margin="normal"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            InputLabelProps={{ style: { color: '#b0bec5' } }}
          />

          <Button
            type="submit"
            variant="contained"
            color="primary"
            fullWidth
            sx={{ mt: 2, fontWeight: 'bold' }}
            disabled={loading}
          >
            {loading ? <CircularProgress size={24} sx={{ color: 'white' }} /> : isLogin ? 'Sign in' : 'Register'}
          </Button>
        </Box>

        <Divider sx={{ my: 2 }} />

        <Button
          variant="contained"
          fullWidth
          sx={{ backgroundColor: '#6e5494', color: 'white', fontWeight: 'bold' }}
          startIcon={<GitHubIcon />}
          onClick={() => (window.location.href = `${process.env.REACT_APP_API_URL}/auth/github/redirect`)}
        >
          Sign in with GitHub
        </Button>

        <Box mt={2}>
          <Typography sx={{ color: '#90caf9' }}>
            {isLogin ? 'New to Blog?' : 'Already have an account?'}{' '}
            <Button 
              onClick={() => navigate(isLogin ? '/auth/register' : '/auth/login')}
              sx={{ color: 'secondary.main', textTransform: 'none' }}
            >
              {isLogin ? 'Create an account' : 'Sign in here'}
            </Button>
          </Typography>
        </Box>
      </Paper>

      <Snackbar open={snackbar.open} autoHideDuration={3000} onClose={handleCloseSnackbar}>
        <Alert onClose={handleCloseSnackbar} severity={snackbar.severity} sx={{ width: '100%' }}>
          {snackbar.message}
        </Alert>
      </Snackbar>
    </Container>
  );
};

export default AuthForm;
