import React, { useEffect, useState } from 'react';
import axios from '../axios';
import { Container, Typography, Button } from '@mui/material';
import Grid2 from '@mui/material/Unstable_Grid2';  // ✅ Use Grid2
import BlogPostCard from './BlogPostCard';
import { useNavigate } from 'react-router-dom';

const Home = () => {
  const [posts, setPosts] = useState([]);
  const [userRole, setUserRole] = useState('');
  const navigate = useNavigate();

  useEffect(() => {
    const role = localStorage.getItem('role');
    setUserRole(role);
    fetchPosts();
  }, []);

  const fetchPosts = async () => {
    try {
      const response = await axios.get('/blog-posts');
      setPosts(response.data.data);
    } catch (error) {
      console.error(error);
    }
  };

  return (
    <Container>
      <Typography variant="h4" gutterBottom>
        Blog Posts
      </Typography>

      {(userRole === 'admin' || userRole === 'editor') && (
        <Button onClick={() => navigate('/create')} variant="contained" color="primary" style={{ marginBottom: '20px' }}>
          Create New Post
        </Button>
      )}

      <Grid2 container spacing={2}>
        {posts.map((post) => (
          <Grid2 item xs={12} sm={6} md={4} key={post.id}>
            <BlogPostCard post={post} userRole={userRole} />
          </Grid2>
        ))}
      </Grid2>
    </Container>
  );
};

export default Home;
