import React, { useState } from 'react';
import {
  Card,
  CardContent,
  Typography,
  Button,
  CardActions,
} from '@mui/material';
import axios from '../axios';
import CommentDialog from './CommentDialog'; // ✅ Import the CommentDialog component

const BlogPostCard = ({ post, userRole, onPostDeleted, onPostUpdated }) => {
  const [editMode, setEditMode] = useState(false);
  const [commentsOpen, setCommentsOpen] = useState(false);

  const handleDelete = async () => {
    try {
      const token = localStorage.getItem('token');
      await axios.delete(`/blog-posts/${post.id}`, {
        headers: { Authorization: `Bearer ${token}` },
      });
      onPostDeleted(post.id);
    } catch (error) {
      console.error('Failed to delete the post:', error);
    }
  };

  return (
    <Card sx={{ backgroundColor: 'background.paper', mb: 2 }}>
      <CardContent>
        <Typography variant="h5" color="primary" gutterBottom>
          {post.title}
        </Typography>
        <Typography variant="body2" color="text.secondary">
          {post.content}
        </Typography>
      </CardContent>
      <CardActions>
        {(userRole === 'admin' || userRole === 'editor') && (
          <>
            <Button onClick={() => setEditMode(true)} color="secondary">
              Edit
            </Button>
            <Button onClick={handleDelete} color="error">
              Delete
            </Button>
          </>
        )}
        <Button onClick={() => setCommentsOpen(true)} color="primary">
          Comments
        </Button>
      </CardActions>

      {/* ✅ Comment Dialog */}
      <CommentDialog postId={post.id} open={commentsOpen} onClose={() => setCommentsOpen(false)} />
    </Card>
  );
};

export default BlogPostCard;
