import React, { useState, useEffect } from "react";
import {
  Card,
  CardContent,
  Typography,
  Button,
  CardActions,
  IconButton,
  Snackbar,
  Alert,
  Dialog,
  DialogTitle,
  DialogContent,
  TextField,
  DialogActions,
} from "@mui/material";
import axios from "../axios";
import CommentDialog from "./CommentDialog"; // ✅ Import the CommentDialog component
import ChatBubbleOutlineIcon from "@mui/icons-material/ChatBubbleOutline"; // ✅ Import Comment Icon

const BlogPostCard = ({ post, userRole, onPostDeleted, onPostUpdated }) => {
  const [editMode, setEditMode] = useState(false);
  const [commentsOpen, setCommentsOpen] = useState(false);
  const [authorName, setAuthorName] = useState("Unknown");

  // Form state for editing
  const [title, setTitle] = useState(post.title);
  const [content, setContent] = useState(post.content);

  // Snackbar state
  const [snackbar, setSnackbar] = useState({
    open: false,
    message: "",
    severity: "success",
  });

  // ✅ Fetch the author's name when the component mounts
  useEffect(() => {
    const fetchAuthor = async () => {
      try {
        const response = await axios.get(`/users/${post.user_id}`);
        setAuthorName(response.data.name);
      } catch (error) {
        console.error("Failed to fetch the author's name:", error);
      }
    };

    if (post.user_id) {
      fetchAuthor();
    }
  }, [post.user_id]);

  const handleDelete = () => {
    const token = localStorage.getItem("token");

    axios
      .delete(`/blog-posts/${post.id}`, {
        headers: { Authorization: `Bearer ${token}` },
      })
      .then(() => {
        setSnackbar({
          open: true,
          message: "Post deleted successfully!",
          severity: "success",
        });
        onPostDeleted(post.id);
      })
      .catch((error) => {
        console.error("Failed to delete the post:", error);
        setSnackbar({
          open: true,
          message: "Failed to delete the post. Please try again.",
          severity: "error",
        });
      });
  };

  // ✅ Handle edit post
  const handleEdit = async () => {
    try {
      const token = localStorage.getItem("token");
      await axios.put(
        `/blog-posts/${post.id}`,
        { title, content },
        {
          headers: { Authorization: `Bearer ${token}` },
        }
      );
      setSnackbar({
        open: true,
        message: "Post updated successfully!",
        severity: "success",
      });
      setEditMode(false);
      onPostUpdated(post.id, { title, content });
    } catch (error) {
      console.error("Failed to update the post:", error);
      setSnackbar({
        open: true,
        message: "Failed to update the post.",
        severity: "error",
      });
    }
  };

  // ✅ Handle closing the snackbar
  const handleCloseSnackbar = () => {
    setSnackbar({ ...snackbar, open: false });
  };

  return (
    <Card sx={{ backgroundColor: "background.paper", mb: 2 }}>
      <CardContent>
        <Typography variant="h5" color="primary" gutterBottom>
          {post.title}
        </Typography>
        <Typography variant="body2" color="text.secondary" noWrap gutterBottom>
          {post.content.substring(0, 100)}...
        </Typography>
        <Typography variant="caption" color="text.secondary">
          Posted by: {authorName}
        </Typography>
      </CardContent>
      

      <CardActions>
      <Button
          size="small"
          color="primary"
          onClick={() => (window.location.href = `/posts/${post.id}`)}
        >
          Read More
        </Button>
        {userRole === "admin" && (
          <>
            <Button onClick={() => setEditMode(true)} color="secondary">
              Edit
            </Button>
            <Button onClick={handleDelete} color="error">
              Delete
            </Button>
          </>
        )}
        {userRole === "editor" && (
          <>
            <Button onClick={() => setEditMode(true)} color="secondary">
              Edit
            </Button>
          </>
        )}
        {/* ✅ Comment Icon */}
        <IconButton onClick={() => setCommentsOpen(true)} color="primary">
          <ChatBubbleOutlineIcon />
        </IconButton>
      </CardActions>

      {/* ✅ Edit Dialog */}
      <Dialog
        open={editMode}
        onClose={() => setEditMode(false)}
        fullWidth
        maxWidth="sm"
      >
        <DialogTitle>Edit Post</DialogTitle>
        <DialogContent>
          <TextField
            label="Title"
            fullWidth
            margin="normal"
            value={title}
            onChange={(e) => setTitle(e.target.value)}
          />
          <TextField
            label="Content"
            fullWidth
            margin="normal"
            multiline
            rows={4}
            value={content}
            onChange={(e) => setContent(e.target.value)}
          />
        </DialogContent>
        <DialogActions>
          <Button onClick={() => setEditMode(false)} color="secondary">
            Cancel
          </Button>
          <Button onClick={handleEdit} variant="contained" color="primary">
            Save
          </Button>
        </DialogActions>
      </Dialog>

      {/* ✅ Comment Dialog */}
      <CommentDialog
        postId={post.id}
        open={commentsOpen}
        onClose={() => setCommentsOpen(false)}
      />

      {/* ✅ Snackbar for feedback */}
      <Snackbar
        open={snackbar.open}
        autoHideDuration={3000}
        onClose={handleCloseSnackbar}
      >
        <Alert
          onClose={handleCloseSnackbar}
          severity={snackbar.severity}
          sx={{ width: "100%" }}
        >
          {snackbar.message}
        </Alert>
      </Snackbar>
    </Card>
  );
};

export default BlogPostCard;
