import React, { useEffect, useState } from "react";
import axios from "../axios";
import {
  Container,
  Typography,
  Button,
  CircularProgress,
  Box,
  Grid,
  Alert,
  Stack,
  IconButton,
  Menu,
  MenuItem,
  Card,
  CardContent,
  CardActions,
  CardMedia,
  Switch,
} from "@mui/material";
import MoreVertIcon from "@mui/icons-material/MoreVert";
import ChatBubbleOutlineIcon from "@mui/icons-material/ChatBubbleOutline";
import { useNavigate } from "react-router-dom";

const Home = () => {
  const [posts, setPosts] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");
  const [anchorEl, setAnchorEl] = useState(null);
  const [selectedPost, setSelectedPost] = useState(null);
  const [userName, setUserName] = useState("Loading...");
  const navigate = useNavigate();

  useEffect(() => {
    fetchUserData();
    fetchPosts();
  }, []);

  const fetchUserData = async () => {
    try {
      const response = await axios.get("/user");
      setUserName(response.data.name);
    } catch (error) {
      setError("Failed to load user data.");
    }
  };

  const fetchPosts = async () => {
    setLoading(true);
    try {
      const response = await axios.get("/blog-posts");
      if (response.data && response.data.data) {
        setPosts(response.data.data);
      } else {
        setPosts([]);
      }
    } catch (error) {
      setError("Failed to load blog posts.");
    } finally {
      setLoading(false);
    }
  };

  const handleMenuClick = (event, post) => {
    setAnchorEl(event.currentTarget);
    setSelectedPost(post);
  };

  const handleMenuClose = () => {
    setAnchorEl(null);
  };

  const handleLogout = () => {
    localStorage.removeItem("token");
    navigate("/auth/login");
  };

  if (loading) {
    return (
      <Box sx={{ display: "flex", justifyContent: "center", height: "60vh" }}>
        <CircularProgress />
      </Box>
    );
  }

  if (error) {
    return <Alert severity="error">{error}</Alert>;
  }

  return (
    <Container>
      <Stack direction="row" justifyContent="space-between" alignItems="center">
        <Typography variant="h6">{userName}</Typography>
        <Stack direction="row" alignItems="center" spacing={2}>
          <Button onClick={handleLogout} variant="contained" color="error">
            Logout
          </Button>
          <Switch />
        </Stack>
      </Stack>
      <Typography variant="h2" gutterBottom align="center">
        THE BLOG
      </Typography>
      <Grid container spacing={3}>
        {posts.map((post) => (
          <Grid item xs={12} md={6} lg={4} key={post.id}>
            <Card>
              <CardMedia
                component="img"
                height="200"
                image={post.image || "https://via.placeholder.com/200"}
                alt={post.title}
              />
              <CardContent>
                <Typography variant="h5" gutterBottom>
                  {post.title}
                </Typography>
                <Typography variant="body2" color="text.secondary" noWrap>
                  {post.content.substring(0, 100)}...
                </Typography>
              </CardContent>
              <CardActions>
                <Button
                  onClick={() => navigate(`/posts/${post.id}`)}
                  variant="contained"
                >
                  Read More
                </Button>
                <IconButton onClick={() => navigate(`/posts/${post.id}`)} color="primary">
                  <ChatBubbleOutlineIcon />
                  <Typography variant="body2" sx={{ ml: 1 }}>
                    {post.comments?.length || 0}
                  </Typography>
                </IconButton>
                <IconButton onClick={(e) => handleMenuClick(e, post)}>
                  <MoreVertIcon />
                </IconButton>
              </CardActions>
            </Card>
          </Grid>
        ))}
      </Grid>
      <Menu anchorEl={anchorEl} open={Boolean(anchorEl)} onClose={handleMenuClose}>
        <MenuItem onClick={() => navigate(`/edit/${selectedPost?.id}`)}>Edit</MenuItem>
        <MenuItem onClick={handleMenuClose}>Delete</MenuItem>
      </Menu>
    </Container>
  );
};

export default Home;
